export const defaultCategory: CategoryInfo = {
  key: "pickup",
  label: "",
  emoji: "",
  color: "systemBlue",
  keywords: []
}

const normalize = (val: any) => {
  if (Array.isArray(val)) return val.join(" ").toLowerCase()
  if (typeof val === "string") return val.toLowerCase()
  return ""
}

export function resolveCategory(content: Record<string, any>): CategoryInfo {
  const { seller, items, category } = content || {}
  const mergedText = [
    normalize(category),
    normalize(seller),
    normalize(items),
  ].join(" ")
  const matched = categoryPresets.find(cfg =>
    cfg.keywords.some(k => mergedText.includes(k.toLowerCase()))
  )
  return matched || defaultCategory
}